// Enhanced Streaming Processor with Block Kit for New Queries
// Processes streaming messages and creates both initial and update Block Kit messages

async function processNewQueryStreaming() {
  const streamingData = $input.all();

  if (!streamingData || streamingData.length === 0) {
    console.log('No streaming data received');
    return [];
  }

  const results = [];

  for (const item of streamingData) {
    if (!item.json.streamingMessages) {
      continue;
    }

    const {
      threadId,
      streamingMessages,
      messageCount,
      executionId,
      streamingFormat
    } = item.json;

    // Get additional context from the original trigger
    const originalMessage = $('On Message Received').item.json;
    const channel = originalMessage.channel;
    const event_ts = originalMessage.event_ts;
    const prompt = originalMessage.text || '';

    // Process each streaming message individually
    for (let i = 0; i < streamingMessages.length; i++) {
      const streamingMessage = streamingMessages[i];
      const isLastMessage = i === streamingMessages.length - 1;

      // Determine if this should trigger an update
      const shouldUpdate = shouldTriggerUpdate(streamingMessage, i, streamingMessages);

      if (shouldUpdate) {
        // Create Block Kit update payload
        const blockKitPayload = createBlockKitMessage(
          streamingMessage,
          streamingMessages.slice(0, i + 1), // All messages up to this point
          threadId,
          originalMessage,
          isLastMessage,
          i === 0 // isInitialMessage
        );

        results.push({
          json: {
            ...blockKitPayload,
            messageIndex: i,
            totalMessages: streamingMessages.length,
            isLastMessage,
            executionId,
            updateType: getUpdateType(streamingMessage),
            routeTo: determineRoute(getUpdateType(streamingMessage), i, isLastMessage),
            priority: getPriority(getUpdateType(streamingMessage)),
            shouldUpdate: true
          }
        });
      }
    }
  }

  return results;
}

/**
 * Determines if a streaming message should trigger a Slack update
 */
function shouldTriggerUpdate(message, index, allMessages) {
  // Always update on first message
  if (index === 0) return true;

  // Always update on last message
  if (index === allMessages.length - 1) return true;

  // Update on important message types
  if (message.type === 'start' || message.type === 'completion' || message.type === 'error') {
    return true;
  }

  // Update on tool usage
  if (message.type === 'tool_use') {
    return true;
  }

  // Update every 5 messages to avoid spam
  if (index % 5 === 0) return true;

  // Update if significant time has passed (more than 3 seconds)
  if (index > 0) {
    const currentTime = new Date(message.timestamp).getTime();
    const lastTime = new Date(allMessages[index - 1].timestamp).getTime();
    if (currentTime - lastTime > 3000) return true;
  }

  return false;
}

/**
 * Creates Block Kit message structure
 */
function createBlockKitMessage(currentMessage, allMessages, threadId, originalMessage, isLastMessage, isInitialMessage) {
  const channel = originalMessage.channel;
  const event_ts = originalMessage.event_ts;
  const prompt = originalMessage.text || '';

  // Determine status
  const status = isLastMessage ? 'completed' : 'running';
  const statusIcon = isLastMessage ? '‚úÖ' : '‚ö°';
  const statusText = isLastMessage ? 'Claude Code execution completed' : 'Claude Code is working...';

  // Create Block Kit structure
  const blocks = [
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `${statusIcon} *${statusText}*`
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": `*Request:*\n${prompt.substring(0, 200)}${prompt.length > 200 ? '...' : ''}`
        },
        {
          "type": "mrkdwn",
          "text": `*Thread ID:*\n\`${threadId}\``
        }
      ]
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Latest Update:*\n${currentMessage.message}`
      }
    }
  ];

  // Add tool usage summary if present
  const toolMessages = allMessages.filter(msg => 
    msg.type === 'tool_use' || 
    msg.message.includes('üîß') || 
    msg.message.includes('üìÅ') ||
    msg.message.includes('üîç') ||
    msg.message.includes('‚úèÔ∏è')
  );

  if (toolMessages.length > 0) {
    const recentTools = toolMessages.slice(-3); // Show last 3 tools
    const toolSummary = recentTools.map(msg => `‚Ä¢ ${msg.message}`).join('\n');

    blocks.push({
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Recent Tools:*\n${toolSummary}${toolMessages.length > 3 ? `\n_...and ${toolMessages.length - 3} more_` : ''}`
      }
    });
  }

  // Add progress context
  const progressText = createProgressText(allMessages, isLastMessage);
  blocks.push({
    "type": "context",
    "elements": [
      {
        "type": "mrkdwn",
        "text": progressText
      }
    ]
  });

  // Add action buttons for completed messages
  if (isLastMessage && status === 'completed') {
    blocks.push({
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "Continue Chat"
          },
          "style": "primary",
          "action_id": "continue_chat_" + threadId
        },
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "View Details"
          },
          "action_id": "view_details_" + threadId
        }
      ]
    });
  }

  return {
    channel: channel,
    thread_ts: event_ts,
    messageType: isInitialMessage ? 'initial' : 'update',
    blocks: JSON.stringify(blocks),
    text: createFallbackText(currentMessage, isLastMessage), // Fallback text
    currentMessage: currentMessage,
    allStreamingMessages: allMessages,
    threadId: threadId,
    timestamp: new Date().toISOString()
  };
}

/**
 * Helper functions
 */
function getUpdateType(message) {
  if (message.type === 'start') return 'start';
  if (message.type === 'completion') return 'completion';
  if (message.type === 'error') return 'error';
  if (message.type === 'tool_use') return 'tool_use';
  return 'progress';
}

function determineRoute(updateType, messageIndex, isLastMessage) {
  if (messageIndex === 0) return 'initial';
  if (isLastMessage) return 'final';
  if (updateType === 'error') return 'error';
  if (updateType === 'tool_use') return 'tool_update';
  return 'progress';
}

function getPriority(updateType) {
  const priorities = {
    'start': 1,
    'error': 1,
    'completion': 1,
    'tool_use': 2,
    'progress': 3
  };
  return priorities[updateType] || 3;
}

function createProgressText(allMessages, isLastMessage) {
  if (isLastMessage) {
    const duration = calculateDuration(allMessages);
    return `‚úÖ Completed with ${allMessages.length} updates${duration ? ` in ${duration}` : ''}`;
  } else {
    const elapsed = getTimeElapsed(allMessages);
    return `‚è≥ ${allMessages.length} updates ‚Ä¢ ${elapsed}`;
  }
}

function calculateDuration(messages) {
  if (messages.length < 2) return null;

  const firstTime = new Date(messages[0].timestamp).getTime();
  const lastTime = new Date(messages[messages.length - 1].timestamp).getTime();
  const duration = Math.round((lastTime - firstTime) / 1000);

  if (duration < 60) {
    return `${duration}s`;
  } else {
    const minutes = Math.floor(duration / 60);
    const seconds = duration % 60;
    return `${minutes}m ${seconds}s`;
  }
}

function getTimeElapsed(messages) {
  if (messages.length < 2) return 'Just started';

  const firstTime = new Date(messages[0].timestamp).getTime();
  const lastTime = new Date(messages[messages.length - 1].timestamp).getTime();
  const elapsed = Math.round((lastTime - firstTime) / 1000);

  if (elapsed < 60) {
    return `${elapsed}s elapsed`;
  } else {
    const minutes = Math.floor(elapsed / 60);
    const seconds = elapsed % 60;
    return `${minutes}m ${seconds}s elapsed`;
  }
}

function createFallbackText(currentMessage, isLastMessage) {
  const prefix = isLastMessage ? '‚úÖ Claude Code completed:' : '‚ö° Claude Code update:';
  return `${prefix} ${currentMessage.message}`;
}

// Main execution
return processNewQueryStreaming();

// Streaming Message Router with Block Kit support
// Routes different types of streaming updates to appropriate handlers

function routeStreamingUpdates() {
  const items = $input.all();

  if (!items || items.length === 0) {
    return [];
  }

  const results = [];

  for (const item of items) {
    const { updateType, messageIndex, isLastMessage, messageType } = item.json;

    // Add routing information while preserving Block Kit data
    const routedItem = {
      json: {
        ...item.json,
        routeTo: determineRoute(updateType, messageIndex, isLastMessage),
        priority: getPriority(updateType),
        shouldUpdate: true,
        // Ensure Block Kit data is preserved
        blocks: item.json.blocks,
        text: item.json.text,
        channel: item.json.channel,
        thread_ts: item.json.thread_ts
      }
    };

    results.push(routedItem);
  }

  return results;
}

function determineRoute(updateType, messageIndex, isLastMessage) {
  if (messageIndex === 0) return 'initial';
  if (isLastMessage) return 'final';
  if (updateType === 'error') return 'error';
  if (updateType === 'tool_use') return 'tool_update';
  return 'progress';
}

function getPriority(updateType) {
  const priorities = {
    'start': 1,
    'error': 1,
    'completion': 1,
    'tool_use': 2,
    'progress': 3
  };
  return priorities[updateType] || 3;
}

return routeStreamingUpdates();

