// Updated Streaming Processor with Initial Message Handling
// Load the initial message handler code
eval(`${$node["Initial Message Handler"].code}`);

// For first message, we'll handle it differently
const streamingData = $input.all();

if (!streamingData || streamingData.length === 0) {
  return [];
}

const results = [];

for (const item of streamingData) {
  if (!item.json.streamingMessages) {
    continue;
  }

  const {
    threadId,
    streamingMessages,
    messageCount,
    executionId
  } = item.json;

  // For the first message, we need to send an initial Block Kit message
  if (streamingMessages.length > 0) {
    const originalMessage = $('On Message Received').item.json;
    const channel = originalMessage.channel;
    const thread_ts = originalMessage.event_ts;
    const prompt = originalMessage.text || '';

    // Create initial message payload
    const firstMessage = streamingMessages[0];
    const initialBlockKit = createInitialBlockKitMessage(prompt, threadId, firstMessage, executionId);

    const initialPayload = {
      json: {
        channel: channel,
        thread_ts: thread_ts,
        blocks: initialBlockKit.blocks,
        text: `ðŸš€ Starting Claude Code: ${prompt.substring(0, 100)}...`,
        reply_broadcast: false,
        streamingMetadata: {
          threadId: threadId,
          executionId: executionId,
          isInitialMessage: true,
          prompt: prompt
        }
      }
    };

    results.push(initialPayload);

    // Also process subsequent messages for routing
    for (let i = 0; i < streamingMessages.length; i++) {
      const streamingMessage = streamingMessages[i];
      const isLastMessage = i === streamingMessages.length - 1;

      // Determine if this should trigger an update
      const shouldUpdate = shouldTriggerUpdate(streamingMessage, i, streamingMessages);

      if (shouldUpdate) {
        const updatePayload = createUpdatePayload(
          streamingMessage,
          streamingMessages.slice(0, i + 1),
          threadId,
          originalMessage,
          isLastMessage
        );

        results.push({
          json: {
            ...updatePayload,
            messageIndex: i,
            totalMessages: streamingMessages.length,
            isLastMessage,
            executionId,
            updateType: getUpdateType(streamingMessage),
            streamingAction: 'update' // Mark as update action
          }
        });
      }
    }
  }
}

return results;

// Helper functions (included from initial-message-handler.js)
function createInitialBlockKitMessage(prompt, threadId, firstMessage, executionId) {
  const blocks = [
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "ðŸš€ *Claude Code Starting...*"
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": `*Request:*\n${prompt.substring(0, 200)}${prompt.length > 200 ? '...' : ''}`
        },
        {
          "type": "mrkdwn",
          "text": `*Thread ID:*\n\`${threadId}\``
        }
      ]
    },
    {
      "type": "divider"
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*Status:*\n${firstMessage.message || 'ðŸ”„ Initializing...'}`
      }
    },
    {
      "type": "context",
      "elements": [
        {
          "type": "mrkdwn",
          "text": `â³ Starting execution â€¢ ${new Date().toLocaleTimeString()}`
        }
      ]
    }
  ];

  return { "blocks": blocks };
}

function shouldTriggerUpdate(message, index, allMessages) {
  if (index === 0) return true;
  if (index === allMessages.length - 1) return true;
  if (message.type === 'start' || message.type === 'completion' || message.type === 'error') return true;
  if (message.type === 'tool_use') return true;
  if (index % 5 === 0) return true;
  if (index > 0) {
    const currentTime = new Date(message.timestamp).getTime();
    const lastTime = new Date(allMessages[index - 1].timestamp).getTime();
    if (currentTime - lastTime > 3000) return true;
  }
  return false;
}

function createUpdatePayload(currentMessage, allMessages, threadId, originalMessage, isLastMessage) {
  const channel = originalMessage.channel;
  const thread_ts = originalMessage.event_ts;
  const prompt = originalMessage.text || '';
  const updateType = getUpdateType(currentMessage);
  const status = isLastMessage ? 'completed' : 'running';

  return {
    channel: channel,
    thread_ts: thread_ts,
    currentMessage: currentMessage,
    allStreamingMessages: allMessages,
    threadId: threadId,
    updateType: updateType,
    timestamp: new Date().toISOString()
  };
}

function getUpdateType(message) {
  if (message.type === 'start') return 'start';
  if (message.type === 'completion') return 'completion';
  if (message.type === 'error') return 'error';
  if (message.type === 'tool_use') return 'tool_use';
  return 'progress';
}

// Load the real-time Block Kit updater code
eval(`${$node["Real-time Block Kit Updater"].code}`);

// Execute the Block Kit update function
return updateBlockKitMessage();

// Continue Thread Block Kit Processor
// Enhanced version for continue operations with Block Kit

const streamingData = $input.all();

if (!streamingData || streamingData.length === 0) {
  return [];
}

const results = [];

for (const item of streamingData) {
  if (!item.json.streamingMessages) {
    continue;
  }

  const {
    threadId,
    streamingMessages,
    messageCount,
    executionId
  } = item.json;

  const originalMessage = $('On Message Received').item.json;
  const channel = originalMessage.channel;
  const thread_ts = originalMessage.event_ts;
  const prompt = originalMessage.text || '';

  // Process streaming messages for continue thread with Block Kit
  for (let i = 0; i < streamingMessages.length; i++) {
    const streamingMessage = streamingMessages[i];
    const isLastMessage = i === streamingMessages.length - 1;

    // Create Block Kit structure for continue operations
    if (i === 0 || isLastMessage || streamingMessage.type === 'tool_use' || i % 3 === 0) {
      const blocks = [
        {
          "type": "section",
          "text": {
            "type": "mrkdwn",
            "text": isLastMessage ? "âœ… *Continue Thread Completed*" : "ðŸ”„ *Continue Thread Update*"
          }
        },
        {
          "type": "section",
          "fields": [
            {
              "type": "mrkdwn",
              "text": `*Follow-up:*\n${prompt.substring(0, 150)}${prompt.length > 150 ? '...' : ''}`
            },
            {
              "type": "mrkdwn",
              "text": `*Thread ID:*\n\`${threadId}\``
            }
          ]
        },
        {
          "type": "divider"
        },
        {
          "type": "section",
          "text": {
            "type": "mrkdwn",
            "text": `*Update:*\n${streamingMessage.message}`
          }
        },
        {
          "type": "context",
          "elements": [
            {
              "type": "mrkdwn",
              "text": isLastMessage ? `âœ… Continue completed with ${i + 1} updates` : `ðŸ”„ ${i + 1} updates â€¢ Continuing...`
            }
          ]
        }
      ];

      const updatePayload = {
        channel: channel,
        thread_ts: thread_ts,
        blocks: blocks,
        text: `Continue: ${streamingMessage.message}`,
        threadId: threadId,
        updateType: 'continue_' + (streamingMessage.type || 'progress'),
        messageIndex: i,
        totalMessages: streamingMessages.length,
        isLastMessage,
        executionId
      };

      results.push({ json: updatePayload });
    }
  }
}

return results;
